<?php

namespace Tests\Feature;

use Illuminate\Foundation\Testing\RefreshDatabase;
use Illuminate\Foundation\Testing\WithFaker;
use Tests\TestCase;

class ValidCampaignTest extends TestCase
{
    public $authuser = "eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9..dTcwFsMlPBujo-8G9Rmlhdq7w69XC9WQ7lKIsoQjLZU";

    /**
     * A basic feature test example.
     *
     * @return void
     */
    public function test_not_valid_campaign()
    {
        $reqbody = array(
            'module_type' => 'flow',
            'name' => ""
        );

        $res = $this->withHeader(
            'Authorization',
            $this->authuser
        )->post('api/campaigns', $reqbody)
            ->assertSuccessful();
            $res=json_decode($res->getContent());
        $this->assertTrue($res->errors[0]=="The name field is required.");
    }
}
